import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import Solid from '../Components/Solid';

function Projects() {
  const [data, setData] = useState([]);
  const [activeProjectId, setActiveProjectId] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);

  const handleActive = (project) => {
    setActiveProjectId(project.id);
    setSelectedProject(project);
  };

  const fetchProjects = async () => {
    const url = "http://localhost:3001/projects";
    const options = {
      method: "GET"
    };

    try {
      const response = await fetch(url, options);
      if (!response.ok) {
        console.error(`HTTP request failed: ${response.status}`);
        return;
      }
      const results = await response.json();
      const modifiedResults = results.projectLists;
      setData(modifiedResults);

      // Set the first project as active by default
      if (modifiedResults.length > 0) {
        setActiveProjectId(modifiedResults[0].id);
        setSelectedProject(modifiedResults[0]);
      }
    } catch (error) {
      console.error(`Failed to fetch projects from ${url}:`, error);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  return (
    <div>
      <div>
        <div className='h-auto lg:h-[400px] bg-black'>
          {selectedProject ? (
            <div className='flex flex-col lg:flex-row text-white justify-between'>
              <div className='px-[20px] lg:px-[30px] py-[20px] lg:py-[30px]'>
                <p className='my-4 text-xl lg:text-2xl font-bold'>{selectedProject.website_name}</p>
                <div className='flex flex-col lg:flex-row gap-3 items-center my-4'>
                  <div className='flex'>
                    <FontAwesomeIcon className='text-yellow-300' icon={faStar} />
                    <FontAwesomeIcon className='text-yellow-300' icon={faStar} />
                    <FontAwesomeIcon className='text-yellow-300' icon={faStar} />
                    <FontAwesomeIcon className='text-yellow-300' icon={faStar} />
                  </div>
                  <p className='px-0 lg:px-4'>2024</p>
                  <p className='px-0 lg:px-4'>Season 1</p>
                </div>
                <div className='my-5'>
                  <p>{selectedProject.website_description}</p>
                </div>
                <div className='my-5 flex flex-wrap gap-4'>
                  <p className='font-semibold'>Key Features:</p>
                  <ul className='list-disc ml-5'>
                    {selectedProject.website_features.map((feature, index) => (
                      <li key={index}>{feature}</li>
                    ))}
                  </ul>
                </div>
                <div className='my-5 flex flex-wrap gap-4'>
                  <p className='font-semibold'>Technical Stack:</p>
                  <ul className='list-disc ml-5'>
                    {selectedProject.technical_stack.map((tech, index) => (
                      <li key={index}>{tech}</li>
                    ))}
                  </ul>
                </div>
                <div className='flex flex-col lg:flex-row gap-4'>
                  <a className='mx-4' href={selectedProject.go_live}>
                    <button className='w-full lg:w-[120px] bg-yellow-500 h-[35px] rounded-[5px] transition-all duration-500 hover:bg-yellow-600'>Go Live</button>
                  </a>
                  <a className='mx-4' href={selectedProject.source_code}>
                    <button className='w-full lg:w-[120px] bg-yellow-500 h-[35px] rounded-[5px] transition-all duration-500 hover:bg-yellow-600'>Source Code</button>
                  </a>
                </div>
              </div>
              <div className='px-[20px] lg:px-[30px] py-[20px] lg:py-[30px]'>
                <div className='w-full lg:w-[400px] h-[200px] lg:h-[300px] bg-white'></div>
              </div>
            </div>
          ) : (
            <p className='text-white text-center py-8'>Select a project to see details</p>
          )}
        </div>
        <div className='bg-black text-white py-4'>
          <div className='py-[20px] lg:py-[30px] px-[20px] lg:px-[30px]'>
            <p className='font-semibold text-center lg:text-left'>Project Lists</p>
          </div>
          <div className='h-auto flex flex-wrap px-[20px] lg:px-[30px] items-center justify-center gap-[15px]'>
            {data.length > 0 ? (
              data.map(project => (
                <div 
                  key={project.id} 
                  className={`w-[150px] lg:w-[260px] h-[150px] lg:h-full text-black p-2 lg:p-4 ${activeProjectId === project.id ? 'border border-yellow-400' : ''}`}
                  onClick={() => handleActive(project)}
                >
                  <img src={project.thumbnail} alt={project.website_name} className='w-full h-full object-cover mt-2 shadow shadow-md shadow-white rounded-[10px]' />
                </div>
              ))
            ) : (
              <p>Loading...</p>
            )}
          </div>
        </div>
      </div>
      <Solid />
    </div>
  );
}

export default Projects;
