import React, { useState } from 'react';
import frontend from '../Assets/Images/frontend.png';

function Services() {
    const [isHovered, setIsHovered] = useState(false);
    const handleHover = ()=>{
        setIsHovered(!isHovered);
    }

    const handleLeave = ()=>{
        setIsHovered(!isHovered);
    }

    const myServices = [
        {id: 1, src:frontend, alt:"frontend"},
        {id: 2},
        {id: 3},
        {id: 4},
        {id: 5},
    ]
  return (
    <div>
        <div>
            <div className='flex justify-between items-center h-[300px] px-[55px]'>
                <p className='font-700 text-[25px]'>I provide the best and<br></br>affordable services</p>
                <div>
                    <p>Services</p>
                    <p>I provide variety of services to my clients.</p>
                </div>
            </div>
        </div>
        <div className='h-[300px] flex-items justify-center px-[35px]'>
            <div className='flex gap-[5px]'>
                {myServices.map(service => (
                    <div key={service.id} className={`w-[300px] h-[220px] bg-black`} 
                    onMouseEnter={handleHover}
                    onMouseLeave={handleLeave}
                    >
                        <img className={`h-0 transition-all duration-[.5s] ${isHovered ? 'h-full' : ''}`} src={service.src} alt={service.alt} />
                    </div>
                ))}

            </div>
        </div>
    </div>
  )
}

export default Services
