import React, { useState } from 'react';
import frontend from '../Assets/Images/frontend.avif';
import responsive from '../Assets/Images/responsive.jpg';

function Services() {
  const myServices = [
    { id: 1, src: frontend, context: "Creating visually stunning websites with a focus on user experience and performance.", alt: "frontend" },
    { id: 2, src: responsive, context: "Crafting adaptable web designs that seamlessly adjust to any device, ensuring an optimal user experience across all screen sizes.", alt: "responsive" },
    { id: 3, src: frontend, alt: "service 3" },
    { id: 4, src: frontend, alt: "service 4" },
    { id: 5, src: frontend, alt: "service 5" },
  ];

  return (
    <div>
      <div className='flex justify-between items-center h-[300px] px-[55px]'>
        <p className='font-bold text-[25px]'>
          I provide the best and<br/>affordable services
        </p>
        <div>
          <p className='font-semibold text-[20px]'>Services</p>
          <p>I provide a variety of services to my clients.</p>
        </div>
      </div>
      <div className='flex justify-center px-[35px]'>
        <div className='flex gap-[20px]'>
          {myServices.map(service => (
            <ServiceItem key={service.id} service={service} />
          ))}
        </div>
      </div>
    </div>
  );
}

function ServiceItem({ service }) {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  return (
    <div
      className='w-[300px] h-[220px] bg-black relative flex items-center justify-center overflow-hidden'
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <img
        className={`h-full w-full transition-all duration-500 ease-in-out object-cover ${isHovered ? 'transform -translate-y-full opacity-0' : 'transform translate-y-0 opacity-100'}`}
        src={service.src}
        alt={service.alt}
      />
      {isHovered && (
        <p className='text-white absolute px-4 transition-opacity duration-500 ease-in-out opacity-100'>
          {service.context}
        </p>
      )}
    </div>
  );
}

export default Services;
